// FAQPage.js
import React from 'react';
import axios from 'axios';
import { useState,useEffect } from 'react';
import './FAQ.css'; // Import the CSS for styling

const faqs = [
  {
    id: 1,
    image: 'data:image/jpeg;base64,', // Replace with your actual image URL
    question: 'How is Tangerine healthy?',
    answer:
      'Tangerine are a great health booster due to their high vitamin C content, which supports the immune system and skin health.',
    fruitName: 'Tangerine',
  },
  {
    id: 2,
    image: 'data:image/jpeg;base64,', // Replace with your actual image URL
    question: 'How is apple healthy?',
    answer:
      'Apples are healthy due to their high fiber content, vitamins, and antioxidants. They aid digestion, support heart health, and help manage weight. Low in calories and rich in nutrients, apples contribute to overall well-being and reduce the risk of chronic diseases.',
    fruitName: 'Apple',
  },
  {
    id: 3,
    image: 'data:image/jpeg;base64,', // Replace with your actual image URL
    question: 'How is lichi healthy?',
    answer:
      'Lychee is healthy due to its high Vitamin C content, which boosts immunity, and antioxidants that protect cells. It also provides essential vitamins and minerals, like potassium, which support heart health and metabolism. Its fiber content aids digestion and promotes overall wellness',
    fruitName: 'Lichi',
  },
];

const FAQ= () => {

   // State to hold the fetched FAQs
   const [faqs, setFaqs] = useState([]);

   // Fetch FAQs from the backend
   useEffect(() => {
     axios
       .get('http://127.0.0.1:8000/faqs') // Replace with your actual API endpoint
       .then((response) => {
         setFaqs(response.data); // Assuming API response contains FAQ data
       })
       .catch((error) => {
         console.error('Error fetching FAQs:', error);
       });
   }, []);
  return (
    <div className="faq-container">
      <h1>FAQ Section</h1>
      {faqs.map((faq) => (
        <div key={faq.id} className="faq-card">
          <img src={faq.image} alt={faq.fruitName} className="faq-image" />
          <div className="faq-content">
            <h2 className="faq-question">{faq.question}</h2>
            <p className="faq-answer">{faq.answer}</p>
            <span className="faq-fruit">{faq.fruitName}</span>
          </div>
        </div>
      ))}
    </div>
  );
};

export default FAQ;
